import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Typography, Paper } from "@mui/material";
import axios from "axios";

const StatsPage = () => {
  const { shortcode } = useParams();
  const [stats, setStats] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/shorturls/${shortcode}`)
      .then(res => setStats(res.data))
      .catch(() => setStats({ error: "Unable to load stats" }));
  }, [shortcode]);

  if (!stats) return <Typography>Loading...</Typography>;
  if (stats.error) return <Typography color="error">{stats.error}</Typography>;

  return (
    <Paper elevation={3} sx={{ padding: 4, mt: 4 }}>
      <Typography variant="h5">Shortcode: {shortcode}</Typography>
      <Typography>Original URL: {stats.originalUrl}</Typography>
      <Typography>Created At: {new Date(stats.createdAt).toLocaleString()}</Typography>
      <Typography>Expiry: {new Date(stats.expiryDate).toLocaleString()}</Typography>
      <Typography>Total Clicks: {stats.clickCount}</Typography>
      <br />
      <Typography variant="h6">Click Details:</Typography>
      {stats.clicks.length === 0 ? (
        <Typography>No clicks yet.</Typography>
      ) : (
        stats.clicks.map((click, i) => (
          <Typography key={i}>
            {new Date(click.timestamp).toLocaleString()} — Referrer: {click.referrer || "N/A"} — Geo: {click.geo || "N/A"}
          </Typography>
        ))
      )}
    </Paper>
  );
};

export default StatsPage;
